  };
  console.log(image);

  return (
    <>
      {image.map((element,index) =>
       
         
            <div>
              <div className="card" style={{ width: "10rem" }}>
                <div style={{ display: "flex" }}>
                  <div>
                    <img
                      className="card-img-top"
                      src={element.download_url}
                      alt="Card image cap"
                    />
