                    <button
                      onClick={() => handleRemovelike(index)}
                      style={{
                        display: "flex",
                        justifyContent: "center",
                        alignItems: "center",
                        marginLeft: "3rem",
                      }}
                      type="button"
                      class="btn btn-dark"
                    >
                      <i class="fa fa-trash"></i>
                    </button>
                  </div>
                </div>
              </div>
            </div>
          
       
      )}
    </>
  );
}
