import React from "react";
import { useContext, useState } from "react";
import { gall } from "../App";
export default function Like() {
  const data = useContext(gall);

  const [remove, setRemove] = useState([]);
  const handleRemovelike = (index2) => {
    console.log(index2);
    data(delete data.index2);
    console.log(remove);
  };
  return (
    <>
      {data.map((i) =>
        i.map((index, item) => {
          return (
            <div>
              <div className="card" style={{ width: "10rem" }}>
                <div style={{ display: "flex" }}>
                  <div>
                    <img
                      className="card-img-top"
                      src={index.download_url}
                      alt="Card image cap"
                    />
                    <h5 className="card-title">{index.author}</h5>
                    <button
                      onClick={() => handleRemovelike(index)}
                      style={{
                        display: "flex",
                        justifyContent: "center",
                        alignItems: "center",
                        marginLeft: "3rem",
                      }}
                      type="button"
                      class="btn btn-dark"
                    >
                      <i class="fa fa-trash"></i>
                    </button>
                  </div>
                </div>
              </div>
            </div>
          );
        })
      )}
    </>
  );
}
