import React from "react";
import { useContext } from "react";
import { gall } from "../App";
export default function Like() {
  const data = useContext(gall);
  console.log(data);
  return (
    <>
      {data.map((i) =>
        i.map((index, item) => {
          return (
            <div style={{ width: "15rem" }}>
              <div>
                {index.id.length > 0 ? (
                  <img
                    className="card-img-top"
                    src={index.download_url}
                    alt="Card image cap"
                    style={{ borderRadius: "1rem" }}
                  />
                ) : (
                  <h1> no data found</h1>
                )}
                {index.author.length == 0 ? null : (
                  <h5 style={{ textAlign: "center" }} class="card-title">
                    {index.author}
                  </h5>
                )}
              </div>
            </div>
          );
        })
      )}
    </>
  );
}
