        i.map((index, item) => {
          return (
            <div>
              <div className="card" style={{ width: "10rem" }}>
                <div style={{ display: "flex" }}>
                  <div>
                    <img
                      className="card-img-top"
                      src={index.download_url}
                      alt="Card image cap"
                    />
                    <h5 className="card-title">{index.author}</h5>
                    <button
                      onClick={() => handleRemove(index)}
                      style={{
                        display: "flex",
                        justifyContent: "center",
                        alignItems: "center",
                        marginLeft: "3rem",
                      }}
                      type="button"
                      class="btn btn-dark"
                    >
                      <i class="fa fa-trash"></i>
                    </button>
                  </div>
                </div>
              </div>
            </div>
          );
        })
      )}
    </>
  );
}
