import "./App.css";
import { useState, useEffect } from "react";
import { createContext } from "react";
import Navbar from "./components/Navbar";
import Like from "./components/Like";
import Dislike from "./components/Dislike";
import { Routes, Route } from "react-router-dom";
const gall = createContext();
const disl = createContext();
function App() {
  const [image, setImage] = useState([]);
  const [like, setLike] = useState([]);
  const [dislike, setdisLike] = useState([]);
  useEffect(() => {
    fetch("https://picsum.photos/v2/list")
      .then((response) => response.json())
      .then((json) =>
        setImage(
          json.map((item) => ({ ...item, isLiked: false, isDisabled: false }))
        )
      );
  }, []);
  const handleLike = (index) => {
    // const obj = image.find((_, idx) => idx === index);
    // console.log(obj);
    // obj.isLiked = !obj.isLiked;
    // const arr = [...image];
    // arr.splice(index, 1, obj);
    // setImage(arr);
  };
  const handleDislike = (index1) => {
    const obj1 = image.find((_, idx1) => idx1 == index1);
    console.log(obj1);
    obj1.isDisabled = !obj1.isDisabled;
    const arr1 = [...image];
    arr1.splice(index1, 1, obj1);
    setImage(arr1);
  };
  return (
    <>
      <gall.Provider value={{ image, setImage }}>
        <disl.Provider value={dislike}>
          <Navbar />
          <Routes>
            <Route
              path="/"
              element={
                <div>
                  {image.map((i, index) => {
                    return (
                      <div>
                        <div className="card" style={{ width: "10rem" }}>
                          <div style={{ display: "flex" }}>
                            <div>
                              <img
                                className="card-img-top"
                                src={i.download_url}
                                alt="Card image cap"
                              />
                              <h5 className="card-title">{i.author}</h5>
                              <button
                                onClick={() => handleLike(index)}
                                type="button"
                                class="btn btn-dark"
                              >
                                <i class="fa fa-regular fa-heart"></i>
                              </button>
                              <button
                                onClick={() => handleDislike(i)}
                                type="button"
                                className="btn btn-dark "
                                style={{ margin: "1rem" }}
                              >
                                <i class="fa fa-thumbs-down"></i>
                              </button>
                            </div>
                          </div>
                        </div>
                      </div>
                    );
                  })}
                </div>
              }
            />
            <Route path="/Like" element={<Like />} />
            <Route path="/Dislike" element={<Dislike />} />
            <Route path="/" element={<App />} />
          </Routes>
        </disl.Provider>
      </gall.Provider>
    </>
  );
}
export default App;
export { gall, disl };
